import { FaTimes } from 'react-icons/fa'

const DELETE_ICON_STYLE = {
  color: 'red',
  cursor: 'pointer',
}



const Task = ({task, onDelete}) => {

  return (
    <div className='task'>
      <h3>
        {task.text} 

        <FaTimes 
          style = {DELETE_ICON_STYLE}
          onClick = {onDelete}
        />
      </h3>
      <p>{task.day}</p>
    </div>
  )
}

export default Task
