const tasks = [
  {
    id: 1,
    text: "Doctor's Appointment",
    day: 'Tuesday',
    reminded: true,
  },
  {
    id: 2,
    text: "Meeting at school",
    day: 'Today',
    reminded: true,
  },
  {
    id: 3,
    text: "Shopping in the mall",
    day: 'Friday',
    reminded: false
  },
]

export const Tasks = () => {
  return (
    <>
    {tasks.map((task) => (
      <h3>{task.text}</h3>
    ))}
    </>
  )
}
 export default Tasks
