export const Tasks = () => {
  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: "Doctor's Appointment",
      day: 'Tuesday',
      reminded: true,
    },
    {
      id: 2,
      text: "Meeting at school",
      day: 'Today',
      reminded: true,
    },
    {
      id: 3,
      text: "Shopping in the mall",
      day: 'Friday',
      reminded: false
    },
  ]);

  return (
    //if you want to add a new object to tasks
    // setTasks(tasks, {...})

    <>
    {tasks.map((task) => (
      <h4 key={task.id}>{task.text}</h4>
    ))}
    </>
  )
}
 export default Tasks
